mapper.select:
SELECT *
FROM CART
WHERE MEMBER_NO= #{memberNo} and PRODUCT_NO = #{PRODUCT_NO};

SELECT CART_NO FROM CART
WHERE MEMBER_NO=#{memberNo}
AND PRODUCT_NO=#{productNo}

SELECT ORDER_QTY
FROM CART
WHERE CART_NO = #{cartNo}

mapper.update:

UPDATE CART
SET ORDER_QTY = #{orderQty}
WHERE CART_NO =#{cartNo}

UPDATE CART
SET
	ORDER_QTY = #{orderQty}
WHERE
	CART_NO = #{cartNo} 
AND
	MEMBER_NO = #{memberNo}
